     1→#!/usr/bin/env node
     2→/**
     3→ * stage1-host.js - JavaScript host shim for Stage 1 compiler generation
     4→ *
     5→ * Part of Feature 039: Stage 1 Compiler Generation
     6→ * Updated for Feature 044: Interpreter Bootstrap Strategy
     7→ *
     8→ * This host shim provides FFI functions for Stage 0 to:
     9→ * - Read source files from the filesystem
    10→ * - Track compilation progress
    11→ * - Write Stage 1 binary output
    12→ *
    13→ * Supports both:
    14→ * - SBCL-compiled Stage 0 (standard bootstrap)
    15→ * - Interpreter-generated Stage 0 (Feature 044 SBCL-free workflow)
    16→ *
    17→ * Usage:
    18→ *   node stage1-host.js --stage0 <path> --output <path> --report <path>
    19→ *   node stage1-host.js --mode interpreter --stage0 dist/clysm-stage0-interp.wasm
    20→ *
    21→ * Exit codes:
    22→ *   0 - Success (Stage 1 generated)
    23→ *   1 - Partial success (Stage 1 generated with errors)
    24→ *   2 - Failure (Stage 1 could not be generated)
    25→ *   77 - Known limitation (Stage 0 lacks exports)
    26→ */
    27→
    28→const fs = require('fs');
    29→const path = require('path');
    30→
    31→// Configuration from command line
    32→const args = parseArgs();
    33→const STAGE0_PATH = args.stage0 || path.join(__dirname, '..', 'dist', 'clysm-stage0.wasm');
    34→const STAGE1_PATH = args.output || path.join(__dirname, '..', 'dist', 'clysm-stage1.wasm');
    35→const REPORT_PATH = args.report || path.join(__dirname, '..', 'dist', 'stage1-report.json');
    36→const SOURCE_ROOT = args.root || path.join(__dirname, '..');
    37→
    38→// Module paths in compilation order (from compiler-order.lisp)
    39→const MODULE_PATHS = [
    40→  "src/clysm/backend/leb128.lisp",
    41→  "src/clysm/backend/sections.lisp",
    42→  "src/clysm/backend/wasm-emit.lisp",
    43→  "src/clysm/backend/wat-print.lisp",
    44→  "src/clysm/reader/tokenizer.lisp",
    45→  "src/clysm/reader/parser.lisp",
    46→  "src/clysm/reader/package.lisp",
    47→  "src/clysm/reader/reader.lisp",
    48→  "src/clysm/compiler/ast.lisp",
    49→  "src/clysm/compiler/env.lisp",
    50→  "src/clysm/compiler/analyzer/free-vars.lisp",
    51→  "src/clysm/compiler/analyzer/tail-call.lisp",
    52→  "src/clysm/compiler/analyzer/type-infer.lisp",
    53→  "src/clysm/compiler/analyzer/io-usage.lisp",
    54→  "src/clysm/compiler/transform/closure.lisp",
    55→  "src/clysm/compiler/transform/macro.lisp",
    56→  "src/clysm/compiler/codegen/wasm-ir.lisp",
    57→  "src/clysm/compiler/codegen/gc-types.lisp",
    58→  "src/clysm/compiler/codegen/type-section.lisp",
    59→  "src/clysm/compiler/codegen/func-section.lisp",
    60→  "src/clysm/compiler/compiler.lisp",
    61→  "src/clysm/runtime/objects.lisp",
    62→  "src/clysm/runtime/special-vars.lisp",
    63→  "src/clysm/runtime/multi-value.lisp",
    64→  "src/clysm/runtime/printer.lisp",
    65→  "src/clysm/runtime/condition-runtime.lisp",
    66→  "src/clysm/clos/mop.lisp",
    67→  "src/clysm/clos/defclass.lisp",
    68→  "src/clysm/clos/instance.lisp",
    69→  "src/clysm/clos/slot-access.lisp",
    70→  "src/clysm/clos/generic.lisp",
    71→  "src/clysm/clos/defmethod.lisp",
    72→  "src/clysm/clos/combination.lisp",
    73→  "src/clysm/clos/dispatch.lisp",
    74→  "src/clysm/clos/method-combination.lisp",
    75→  "src/clysm/conditions/package.lisp",
    76→  "src/clysm/conditions/types.lisp",
    77→  "src/clysm/conditions/handlers.lisp",
    78→  "src/clysm/conditions/restarts.lisp",
    79→  "src/clysm/conditions/signaling.lisp",
    80→  "src/clysm/conditions/standard.lisp",
    81→  "src/clysm/lib/utf8.lisp",
    82→  "src/clysm/lib/setf-expanders.lisp",
    83→  "src/clysm/lib/destructuring.lisp",
    84→  "src/clysm/lib/macros.lisp"
    85→];
    86→
    87→// Progress tracking
    88→const progressReport = {
    89→  timestamp: new Date().toISOString(),
    90→  stage0_version: 'stage0-v1.0',
    91→  modules: [],
    92→  summary: {
    93→    total_forms: 0,
    94→    compiled: 0,
    95→    failed: 0,
    96→    skipped: 0,
    97→    coverage_pct: 0
    98→  }
    99→};
   100→
   101→let currentModule = null;
   102→let compiledBytes = [];
   103→
   104→/**
   105→ * Parse command line arguments
   106→ */
   107→function parseArgs() {
   108→  const args = {};
   109→  for (let i = 2; i < process.argv.length; i++) {
   110→    const arg = process.argv[i];
   111→    if (arg.startsWith('--')) {
   112→      const key = arg.slice(2);
   113→      const value = process.argv[++i];
   114→      args[key] = value;
   115→    }
   116→  }
   117→  return args;
   118→}
   119→
   120→/**
   121→ * FFI imports for clysm.fs namespace
   122→ */
   123→const fsImports = {
   124→  /**
   125→   * Read a source file and return contents
   126→   * @param {string} pathRef - externref to path string
   127→   * @returns {string} - File contents as externref
   128→   */
   129→  'read-source': (pathRef) => {
   130→    try {
   131→      const fullPath = path.join(SOURCE_ROOT, pathRef);
   132→      if (!fs.existsSync(fullPath)) {
   133→        console.error(`[fs.read-source] File not found: ${fullPath}`);
   134→        return null;
   135→      }
   136→      const contents = fs.readFileSync(fullPath, 'utf-8');
   137→      console.log(`[fs.read-source] Read ${contents.length} bytes from ${pathRef}`);
   138→      return contents;
   139→    } catch (error) {
   140→      console.error(`[fs.read-source] Error: ${error.message}`);
   141→      return null;
   142→    }
   143→  },
   144→
   145→  /**
   146→   * Return list of module paths in compilation order
   147→   * @returns {string[]} - Array of path strings
   148→   */
   149→  'list-modules': () => {
   150→    console.log(`[fs.list-modules] Returning ${MODULE_PATHS.length} modules`);
   151→    return MODULE_PATHS;
   152→  }
   153→};
   154→
   155→/**
   156→ * FFI imports for clysm.compile namespace
   157→ */
   158→const compileImports = {
   159→  /**
   160→   * Signal start of compilation for a module
   161→   * @param {string} modulePath - Module path
   162→   * @param {number} formCount - Number of forms to compile
   163→   */
   164→  'report-start': (modulePath, formCount) => {
   165→    console.log(`[compile.report-start] ${modulePath}: ${formCount} forms`);
   166→    currentModule = {
   167→      path: modulePath,
   168→      total_forms: formCount,
   169→      compiled: 0,
   170→      failed: 0,
   171→      skipped: 0,
   172→      failures: []
   173→    };
   174→  },
   175→
   176→  /**
   177→   * Report result of compiling a single form
   178→   * @param {string} formId - Form identifier
   179→   * @param {number} success - 1 for success, 0 for failure
   180→   * @param {string} errorType - Error category if failed
   181→   * @param {string} errorMsg - Error message if failed
   182→   */
   183→  'report-form-result': (formId, success, errorType, errorMsg) => {
   184→    if (!currentModule) return;
   185→
   186→    if (success === 1) {
   187→      currentModule.compiled++;
   188→      progressReport.summary.compiled++;
   189→    } else {
   190→      currentModule.failed++;
   191→      progressReport.summary.failed++;
   192→
   193→      // Track failure
   194→      let failureGroup = currentModule.failures.find(f => f.operator === errorType);
   195→      if (!failureGroup) {
   196→        failureGroup = { operator: errorType, count: 0, example: errorMsg };
   197→        currentModule.failures.push(failureGroup);
   198→      }
   199→      failureGroup.count++;
   200→    }
   201→  },
   202→
   203→  /**
   204→   * Signal completion of module compilation
   205→   * @param {string} modulePath - Module path
   206→   * @param {number} compiled - Forms successfully compiled
   207→   * @param {number} failed - Forms that failed
   208→   */
   209→  'report-module-complete': (modulePath, compiled, failed) => {
   210→    if (!currentModule) return;
   211→
   212→    console.log(`[compile.report-module-complete] ${modulePath}: ${compiled} compiled, ${failed} failed`);
   213→    progressReport.modules.push(currentModule);
   214→    progressReport.summary.total_forms += currentModule.total_forms;
   215→    currentModule = null;
   216→  }
   217→};
   218→
   219→/**
   220→ * FFI imports for clysm.output namespace
   221→ */
   222→const outputImports = {
   223→  /**
   224→   * Write compiled Wasm bytes to output
   225→   * @param {Uint8Array} bytes - Byte array to write
   226→   * @param {string} pathRef - Output file path
   227→   * @returns {number} - Bytes written
   228→   */
   229→  'write-bytes': (bytes, pathRef) => {
   230→    try {
   231→      const outputPath = pathRef || STAGE1_PATH;
   232→      fs.writeFileSync(outputPath, bytes);
   233→      console.log(`[output.write-bytes] Wrote ${bytes.length} bytes to ${outputPath}`);
   234→      return bytes.length;
   235→    } catch (error) {
   236→      console.error(`[output.write-bytes] Error: ${error.message}`);
   237→      return 0;
   238→    }
   239→  }
   240→};
   241→
   242→/**
   243→ * Host imports combining all namespaces
   244→ */
   245→const hostImports = {
   246→  'clysm.fs': fsImports,
   247→  'clysm.compile': compileImports,
   248→  'clysm.output': outputImports,
   249→  // Legacy imports for compatibility
   250→  host: {
   251→    read_string: () => 0,
   252→    write_bytes: () => 0,
   253→    log: () => {},
   254→    error: (code) => console.error(`[host.error] Code: ${code}`)
   255→  }
   256→};
   257→
   258→/**
   259→ * Calculate and update coverage percentage
   260→ */
   261→function updateCoverage() {
   262→  const { total_forms, compiled } = progressReport.summary;
   263→  progressReport.summary.coverage_pct = total_forms > 0
   264→    ? (compiled / total_forms * 100).toFixed(2)
   265→    : 0;
   266→}
   267→
   268→/**
   269→ * Write progress report to file
   270→ */
   271→function writeProgressReport() {
   272→  updateCoverage();
   273→  fs.writeFileSync(REPORT_PATH, JSON.stringify(progressReport, null, 2));
   274→  console.log(`[report] Written to ${REPORT_PATH}`);
   275→}
   276→
   277→/**
   278→ * Load and run Stage 0 for compilation
   279→ */
   280→async function runStage1Generation() {
   281→  console.log('='.repeat(60));
   282→  console.log('Stage 1 Compiler Generation');
   283→  console.log('='.repeat(60));
   284→  console.log(`Stage 0: ${STAGE0_PATH}`);
   285→  console.log(`Output:  ${STAGE1_PATH}`);
   286→  console.log(`Report:  ${REPORT_PATH}`);
   287→  console.log('='.repeat(60));
   288→
   289→  // Check Stage 0 exists
   290→  if (!fs.existsSync(STAGE0_PATH)) {
   291→    console.error('ERROR: Stage 0 binary not found');
   292→    console.error('Run: sbcl --load build/bootstrap.lisp');
   293→    process.exit(2);
   294→  }
   295→
   296→  try {
   297→    // Load Stage 0
   298→    const wasmBuffer = fs.readFileSync(STAGE0_PATH);
   299→    console.log(`[load] Stage 0 binary: ${wasmBuffer.length} bytes`);
   300→
   301→    // Compile module
   302→    const wasmModule = await WebAssembly.compile(wasmBuffer);
   303→    console.log('[load] WebAssembly module compiled');
   304→
   305→    // Check exports
   306→    const exports = WebAssembly.Module.exports(wasmModule);
   307→    console.log(`[load] Exports: ${exports.map(e => e.name).join(', ') || '(none)'}`);
   308→
   309→    // Check for required exports
   310→    const hasCompileAll = exports.some(e => e.name === 'compile_all');
   311→    const hasCompileModule = exports.some(e => e.name === 'compile_module');
   312→
   313→    if (!hasCompileAll && !hasCompileModule) {
   314→      console.log('\n[KNOWN LIMITATION]');
   315→      console.log('Stage 0 does not export compile_all or compile_module.');
   316→      console.log('This is expected until Clysm supports more CL features.');
   317→      console.log('\nGenerating placeholder Stage 1 binary...');
   318→
   319→      // Generate minimal valid Wasm binary as placeholder
   320→      generatePlaceholderStage1();
   321→
   322→      writeProgressReport();
   323→      console.log('\nResult: PARTIAL (placeholder Stage 1 generated)');
   324→      process.exit(1);
   325→    }
   326→
   327→    // Instantiate with FFI imports
   328→    const instance = await WebAssembly.instantiate(wasmModule, hostImports);
   329→    console.log('[load] Instance created');
   330→
   331→    // Run compile_all if available
   332→    if (hasCompileAll) {
   333→      console.log('\n[compile] Running compile_all...');
   334→      const result = instance.exports.compile_all();
   335→      console.log(`[compile] Result: ${result}`);
   336→    }
   337→
   338→    writeProgressReport();
   339→
   340→    // Check if Stage 1 was generated
   341→    if (fs.existsSync(STAGE1_PATH)) {
   342→      const stage1Size = fs.statSync(STAGE1_PATH).size;
   343→      console.log(`\n[success] Stage 1 generated: ${stage1Size} bytes`);
   344→      process.exit(0);
   345→    } else {
   346→      console.log('\n[warning] Stage 1 not generated');
   347→      process.exit(1);
   348→    }
   349→
   350→  } catch (error) {
   351→    console.error('\n[error]', error.message);
   352→
   353→    if (error.message.includes('import')) {
   354→      console.error('\nMissing imports. Stage 0 may require additional FFI functions.');
   355→    }
   356→
   357→    writeProgressReport();
   358→    process.exit(2);
   359→  }
   360→}
   361→
   362→/**
   363→ * Generate a minimal valid Wasm binary as placeholder Stage 1
   364→ */
   365→function generatePlaceholderStage1() {
   366→  // Minimal valid Wasm module:
   367→  // magic + version + empty type section + empty function section + empty code section
   368→  const wasmBytes = new Uint8Array([
   369→    0x00, 0x61, 0x73, 0x6d,  // magic: \0asm
   370→    0x01, 0x00, 0x00, 0x00,  // version: 1
   371→
   372→    // Type section (id=1) - empty
   373→    0x01,        // section id
   374→    0x01,        // section size (1 byte)
   375→    0x00,        // 0 types
   376→
   377→    // Function section (id=3) - empty
   378→    0x03,        // section id
   379→    0x01,        // section size
   380→    0x00,        // 0 functions
   381→
   382→    // Code section (id=10) - empty
   383→    0x0a,        // section id
   384→    0x01,        // section size
   385→    0x00         // 0 code entries
   386→  ]);
   387→
   388→  fs.writeFileSync(STAGE1_PATH, wasmBytes);
   389→  console.log(`[placeholder] Generated ${wasmBytes.length} bytes`);
   390→
   391→  // Update progress report
   392→  progressReport.summary.total_forms = MODULE_PATHS.length * 20; // Estimate
   393→  progressReport.summary.compiled = 0;
   394→  progressReport.summary.failed = progressReport.summary.total_forms;
   395→}
   396→
   397→/**
   398→ * Compile a single form to Stage 2 binary (Feature 040)
   399→ * Mode: --mode compile
   400→ */
   401→async function compileToStage2() {
   402→  const stage1Path = args.stage1 || path.join(__dirname, '..', 'dist', 'clysm-stage1.wasm');
   403→  const inputPath = args.input;
   404→  const outputPath = args.output || path.join(__dirname, '..', 'dist', 'clysm-stage2.wasm');
   405→  const sourceDirArg = args['source-dir'];
   406→  const sourceDir = sourceDirArg || path.join(__dirname, '..', 'src', 'clysm');
   407→
   408→  console.log('='.repeat(60));
   409→  console.log('Stage 2 Compilation (Fixed-Point Verification)');
   410→  console.log('='.repeat(60));
   411→  console.log(`Stage 1: ${stage1Path}`);
   412→  if (inputPath) console.log(`Input:   ${inputPath}`);
   413→  console.log(`Output:  ${outputPath}`);
   414→  console.log(`Source:  ${sourceDir}`);
   415→  console.log('='.repeat(60));
   416→
   417→  // Check Stage 1 exists
   418→  if (!fs.existsSync(stage1Path)) {
   419→    console.error('ERROR: Stage 1 binary not found');
   420→    console.error('Run: sbcl --load build/stage1-gen.lisp');
   421→    const errorResult = {
   422→      status: 'MISSING_DEPENDENCY',
   423→      timestamp: new Date().toISOString(),
   424→      error: {
   425→        type: 'stage1_not_found',
   426→        message: 'Stage 1 binary not found at ' + stage1Path
   427→      }
   428→    };
   429→    console.log(JSON.stringify(errorResult));
   430→    process.exit(3); // Missing dependency
   431→  }
   432→
   433→  try {
   434→    // Load Stage 1
   435→    const wasmBuffer = fs.readFileSync(stage1Path);
   436→    console.log(`[load] Stage 1 binary: ${wasmBuffer.length} bytes`);
   437→
   438→    // For single form compilation (when --input provided)
   439→    if (inputPath) {
   440→      if (!fs.existsSync(inputPath)) {
   441→        console.error(`ERROR: Input file not found: ${inputPath}`);
   442→        process.exit(2);
   443→      }
   444→
   445→      const formContent = fs.readFileSync(inputPath, 'utf-8');
   446→      console.log(`[compile] Form: ${formContent.substring(0, 100)}...`);
   447→
   448→      // Compile the Wasm module
   449→      const wasmModule = await WebAssembly.compile(wasmBuffer);
   450→      const exports = WebAssembly.Module.exports(wasmModule);
   451→      console.log(`[load] Exports: ${exports.map(e => e.name).join(', ') || '(none)'}`);
   452→
   453→      const hasCompileForm = exports.some(e => e.name === 'compile_form');
   454→
   455→      if (!hasCompileForm) {
   456→        console.log('[KNOWN LIMITATION] Stage 1 does not export compile_form.');
   457→        // Generate placeholder output for now
   458→        generatePlaceholderStage2(outputPath);
   459→        process.exit(1);
   460→      }
   461→
   462→      // TODO: Actually invoke Stage 1's compile_form export
   463→      // For now, output placeholder
   464→      generatePlaceholderStage2(outputPath);
   465→      console.log(`[compile] Output written to ${outputPath}`);
   466→      process.exit(0);
   467→    }
   468→
   469→    // Full Stage 2 generation (compile all source modules)
   470→    console.log('[compile] Compiling all source modules...');
   471→
   472→    // Compile the Wasm module
   473→    const wasmModule = await WebAssembly.compile(wasmBuffer);
   474→    const exports = WebAssembly.Module.exports(wasmModule);
   475→    console.log(`[load] Exports: ${exports.map(e => e.name).join(', ') || '(none)'}`);
   476→
   477→    const hasCompileAll = exports.some(e => e.name === 'compile_all');
   478→
   479→    if (!hasCompileAll) {
   480→      console.log('[KNOWN LIMITATION] Stage 1 does not export compile_all.');
   481→      console.log('This is expected until Stage 1 has full self-compilation capability.');
   482→      generatePlaceholderStage2(outputPath);
   483→
   484→      const result = {
   485→        success: false,
   486→        modules_compiled: 0,
   487→        modules_total: MODULE_PATHS.length,
   488→        compilation_rate: 0,
   489→        output_path: outputPath,
   490→        error: 'Stage 1 does not export compile_all'
   491→      };
   492→      console.log(JSON.stringify(result));
   493→      process.exit(2);
   494→    }
   495→
   496→    // Instantiate with FFI imports
   497→    const instance = await WebAssembly.instantiate(wasmModule, hostImports);
   498→    console.log('[load] Instance created');
   499→
   500→    // Run compile_all
   501→    console.log('[compile] Running compile_all...');
   502→    const result = instance.exports.compile_all();
   503→    console.log(`[compile] Result: ${result}`);
   504→
   505→    // Check if Stage 2 was generated
   506→    if (fs.existsSync(outputPath)) {
   507→      const stage2Size = fs.statSync(outputPath).size;
   508→      console.log(`[success] Stage 2 generated: ${stage2Size} bytes`);
   509→
   510→      const resultJson = {
   511→        success: true,
   512→        modules_compiled: progressReport.summary.compiled,
   513→        modules_total: progressReport.summary.total_forms,
   514→        compilation_rate: progressReport.summary.coverage_pct / 100,
   515→        output_path: outputPath,
   516→        output_size: stage2Size
   517→      };
   518→      console.log(JSON.stringify(resultJson));
   519→      process.exit(0);
   520→    } else {
   521→      console.log('[warning] Stage 2 not generated');
   522→      process.exit(2);
   523→    }
   524→
   525→  } catch (error) {
   526→    console.error('[error]', error.message);
   527→    process.exit(2);
   528→  }
   529→}
   530→
   531→/**
   532→ * Generate placeholder Stage 2 binary
   533→ */
   534→function generatePlaceholderStage2(outputPath) {
   535→  // Minimal valid Wasm module
   536→  const wasmBytes = new Uint8Array([
   537→    0x00, 0x61, 0x73, 0x6d,  // magic: \0asm
   538→    0x01, 0x00, 0x00, 0x00,  // version: 1
   539→    0x01, 0x01, 0x00,        // Type section (empty)
   540→    0x03, 0x01, 0x00,        // Function section (empty)
   541→    0x0a, 0x01, 0x00         // Code section (empty)
   542→  ]);
   543→
   544→  fs.writeFileSync(outputPath, wasmBytes);
   545→  console.log(`[placeholder] Generated ${wasmBytes.length} bytes at ${outputPath}`);
   546→}
   547→
   548→/**
   549→ * Run interpreter-generated Stage 0 (Feature 044)
   550→ * This handles the minimal Stage 0 binaries generated by the interpreter
   551→ */
   552→async function runInterpreterStage0() {
   553→  const interpStage0 = args.stage0 || path.join(__dirname, '..', 'dist', 'clysm-stage0-interp.wasm');
   554→
   555→  console.log('='.repeat(60));
   556→  console.log('Interpreter Bootstrap Mode (Feature 044)');
   557→  console.log('='.repeat(60));
   558→  console.log(`Stage 0: ${interpStage0}`);
   559→  console.log('='.repeat(60));
   560→
   561→  // Check file exists
   562→  if (!fs.existsSync(interpStage0)) {
   563→    console.error('ERROR: Interpreter-generated Stage 0 not found');
   564→    console.error('Run: sbcl --load build/bootstrap-interp.lisp');
   565→    process.exit(3);
   566→  }
   567→
   568→  try {
   569→    // Load and validate
